import os
import base64

# This is a base64-encoded WAV file containing a short futuristic sound
WAKE_SOUND = """

"""


def create_wake_sound(sounds_dir: str):
    """Create the wake sound file in the sounds directory"""
    if not os.path.exists(sounds_dir):
        os.makedirs(sounds_dir)

    wake_sound_path = os.path.join(sounds_dir, "wake.wav")
    if not os.path.exists(wake_sound_path):
        # Decode and write the sound file
        sound_data = base64.b64decode(WAKE_SOUND.strip())
        with open(wake_sound_path, "wb") as f:
            f.write(sound_data)


if __name__ == "__main__":
    # Create sound in the sounds directory
    sounds_dir = os.path.join(os.path.dirname(os.path.dirname(__file__)), "sounds")
    create_wake_sound(sounds_dir)
